/**
 * Main entry point for the Google Cloud Application Integration JavaScript Task.
 * This function MUST be named executeScript and be at the top level.
 * @param {IntegrationEvent} event - The event object provided by the platform, used for logging and parameter handling.
 */
function executeScript(event) {

  /**
   * Parses the input, which can be a JSON string or a pre-parsed object.
   * This is a helper function.
   * @param {string | object} input - The input data.
   * @returns {object} The parsed JavaScript object.
   * @throws {Error} If the input is invalid or cannot be parsed.
   */
  function parseInput(input) {
    try {
      if (typeof input === 'string') {
        return JSON.parse(input);
      } else if (typeof input === 'object' && input !== null) {
        return input;
      } else {
        throw new Error("Invalid input: Must be a JSON object or string");
      }
    } catch (error) {
      // Re-throw the error to be caught by the main try/catch block
      throw new Error(`Failed to parse JSON: ${error.message}`);
    }
  }

  /**
   * Transforms a specific API Management export format into an OpenAPI 3.0.4 specification.
   * This is the core logic helper function.
   * @param {object} input - The parsed API data object.
   * @returns {object} The OpenAPI specification object.
   */
  function transformToOpenAPI(input) {
    const output = {
      openapi: "3.0.4",
      info: {
        title: input.name || "API",
        description: input.description || "",
        termsOfService: input.termsOfServiceUrl || undefined,
        contact: input.contact ? { email: input.contact.email || undefined, name: input.contact.name || undefined, url: input.contact.url || undefined } : undefined,
        license: input.license ? { name: input.license.name || "Apache 2.0", url: input.license.url || undefined } : undefined,
        version: input.apiRevision || "1.0.0",
      },
      servers: input.serviceUrl ? [{ url: input.serviceUrl }] : [],
      tags: (input.tagDescriptions && Array.isArray(input.tagDescriptions)) ? input.tagDescriptions.map(tag => ({ name: tag.name, description: tag.description || "", externalDocs: tag.externalDocsUrl ? { description: tag.externalDocsDescription || `Find out more about ${tag.name}`, url: tag.externalDocsUrl } : undefined })) : [],
      paths: {},
      components: {
        schemas: {},
        securitySchemes: (input.authenticationSettings && input.authenticationSettings.subscriptionKeyRequired) ? { api_key: { type: "apiKey", name: (input.subscriptionKeyParameterNames && input.subscriptionKeyParameterNames.header) || "Ocp-Apim-Subscription-Key", in: "header" } } : {}
      }
    };

    if (input.schemas && input.schemas.value && input.schemas.value[0] && input.schemas.value[0].document && input.schemas.value[0].document.components && input.schemas.value[0].document.components.schemas) {
      const schemas = input.schemas.value[0].document.components.schemas;
      Object.entries(schemas).forEach(([name, schema]) => { if (!schema['x-apim-inline']) { output.components.schemas[name] = schema; } });
    }

    if (input.operations && input.operations.value) {
      input.operations.value.forEach(op => {
        const path = op.urlTemplate;
        const method = op.method.toLowerCase();
        if (!output.paths[path]) { output.paths[path] = {}; }

        const operation = {
          tags: (op.tags && Array.isArray(op.tags)) ? op.tags.map(t => t.name) : [],
          summary: op.name || "",
          description: op.description || "",
          operationId: op.id.split('/').pop(),
          parameters: [],
          responses: {}
        };

        if (op.templateParameters && op.templateParameters.length) { operation.parameters.push(...op.templateParameters.map(param => ({ name: param.name, in: "path", description: param.description || "", required: param.required !== false, schema: { type: param.type, format: param.format || undefined, enum: (param.values && param.values.length) ? param.values : undefined } }))); }
        if (op.request && op.request.queryParameters && op.request.queryParameters.length) { operation.parameters.push(...op.request.queryParameters.map(param => ({ name: param.name, in: "query", description: param.description || "", required: param.required || false, schema: { type: param.type, format: param.format || undefined, default: param.defaultValue || undefined, enum: (param.values && param.values.length) ? param.values : undefined, items: param.type === 'array' ? { type: 'string' } : undefined } }))); }
        if (op.request && op.request.headers && op.request.headers.length) { operation.parameters.push(...op.request.headers.map(header => ({ name: header.name, in: "header", description: header.description || "", required: header.required || false, schema: { type: header.type, format: header.format || undefined } }))); }

        if (op.request && op.request.representations && op.request.representations.length) {
          operation.requestBody = {
            description: op.request.description || "",
            content: {},
            required: op.request.representations.some(rep => rep.formParameters && rep.formParameters.some(p => p.required))
          };
          op.request.representations.forEach(rep => {
            let schema;
            const schemaSource = input.schemas && input.schemas.value && input.schemas.value[0] && input.schemas.value[0].document && input.schemas.value[0].document.components && input.schemas.value[0].document.components.schemas;
            if (rep.contentType === "application/octet-stream") { schema = { type: "string", format: "binary" }; }
            else if (rep.typeName && output.components.schemas[rep.typeName]) { schema = { $ref: `#/components/schemas/${rep.typeName}` }; }
            else if (rep.schemaId && schemaSource && schemaSource[rep.schemaId]) { schema = schemaSource[rep.schemaId]; }
            else if (rep.formParameters && rep.formParameters.length) {
              const requiredParams = rep.formParameters.filter(p => p.required).map(p => p.name);
              schema = {
                type: "object",
                properties: rep.formParameters.reduce((props, param) => {
                  // --- FIX APPLIED HERE ---
                  // 1. Create the base schema for the parameter.
                  const paramSchema = {
                    type: param.type,
                    format: param.format,
                    description: param.description,
                    enum: (param.values && param.values.length) ? param.values : undefined
                  };

                  // 2. If the type is 'array', add the required 'items' field.
                  if (param.type === 'array') {
                    paramSchema.items = { type: 'string' }; // Assuming form arrays contain strings
                  }

                  // 3. Assign the now-valid schema to the property.
                  props[param.name] = paramSchema;
                  return props;
                }, {})
              };
              if (requiredParams.length > 0) { schema.required = requiredParams; }
            } else { schema = { type: "object" }; }
            operation.requestBody.content[rep.contentType] = { schema };
          });
        }

        if (op.responses && op.responses.length) {
          op.responses.forEach(resp => {
            const response = { description: resp.description || "No description" };
            const schemaSource = input.schemas && input.schemas.value && input.schemas.value[0] && input.schemas.value[0].document && input.schemas.value[0].document.components && input.schemas.value[0].document.components.schemas;
            if (resp.representations && resp.representations.length) {
              response.content = {};
              resp.representations.forEach(rep => {
                let schema;
                if (rep.typeName && output.components.schemas[rep.typeName]) { schema = { $ref: `#/components/schemas/${rep.typeName}` }; }
                else if (rep.schemaId && schemaSource && schemaSource[rep.schemaId]) { schema = schemaSource[rep.schemaId]; }
                else { schema = { type: rep.type || "object" }; }
                response.content[rep.contentType] = { schema };
              });
            }
            if (resp.headers && resp.headers.length) {
              response.headers = {};
              resp.headers.forEach(header => { response.headers[header.name] = { description: header.description || "", schema: { type: header.type, format: header.format || undefined } }; });
            }
            operation.responses[resp.statusCode || "default"] = response;
          });
        }
        output.paths[path][method] = operation;
      });
    }

    // Clean up empty objects for a tidier output
    if (output.info.contact) { Object.keys(output.info.contact).forEach(key => { if (output.info.contact[key] === undefined) delete output.info.contact[key]; }); if (Object.keys(output.info.contact).length === 0) delete output.info.contact; }
    if (!output.info.termsOfService) delete output.info.termsOfService;
    if (output.info.license && !output.info.license.url) delete output.info.license.url;
    if (Object.keys(output.components.securitySchemes).length === 0) delete output.components.securitySchemes;
    if (Object.keys(output.components.schemas).length === 0) delete output.components.schemas;

    return output;
  }

  // --- Main execution logic ---
  try {
    event.log("STEP 1: Starting the transformation script.", "INFO");

    const inputData = event.getParameter("openaispecs-list");
    event.log("STEP 2: Attempted to get input parameter 'openaispecs-list'.", "INFO");

    if (!inputData) {
        throw new Error("Input parameter 'openaispecs-list' was not found or is empty. Check variable name and task mappings.");
    }
    event.log("STEP 3: Input parameter was found and is not empty.", "INFO");

    const parsedInput = parseInput(inputData);
    event.log("STEP 4: Successfully parsed the input string into a JSON object.", "INFO");

    const specJson = transformToOpenAPI(parsedInput);
    event.log("STEP 5: Successfully transformed the JSON object into a valid OpenAPI Spec object.", "INFO");

    event.setParameter("specJson", specJson);
    event.log("STEP 6: Script completed successfully. 'specJson' parameter has been set.", "INFO");

  } catch (error) {
    event.log(`EXECUTION FAILED: The script stopped because of an error. REASON: ${error.message}`, "ERROR");
    // This line ensures the task is officially marked as FAILED in the execution logs.
    throw error;
  }
}
